class ChessDataset(Dataset):
    def __init__(self):
        data = np.load('./data/preprocessed/chess_data1.npz')
        self.X = data['arr_0']
        self.y = data['arr_1']

        print(f'[ Data Loaded ==> X {self.X.shape} Y {self.y.shape} ]')

    def __len__(self):
        return self.X.shape[0]
    
    def __getitem__(self, idx):
        return(self.X[idx], self.Y[idx])

def get_closest(num: int) -> int:
    labels = [-1, 0, 1]

    lowest_dist = float('inf')
    best_idx = 0
    for i in range(len(labels)):
        dist = abs(num - labels[i])
        if dist < lowest_dist:
            best_idx = i
            lowest_dist = dist
    
    return labels[best_idx]

